// general purpose utilities...

export const assert = (value, msg = undefined) => 
{
	let error = "Assertion Error: (" + value + ")";
	if (msg) error += "Failed Test ("  + msg + ")";

	if ( !value ) throw new Error(msg);
	return value;
}
