import React from 'react';

import Deck from './Deck';

class App extends React.Component {
    deck = undefined;

    constructor() {
        super();
        this.state = { canRender: false, deckId: undefined };
    }

    async componentDidMount() {
        this.deck = new Deck();
        let id = await this.deck.newDeck();
        this.setState({ canRender: true, deckId: id })

        console.log("DID MOUNT: ", this.state.deckId)

    }

    testDeck = async () =>
    {
        const MAX=20;
        const dref = await this.deck.newDeck();
        console.log("dref: ", dref);

        for (let i=0; i<MAX; i++) {
            let card = await this.deck.drawCard();
            console.log(card);
        }
    }

    render() {
        let msg = 'Nothing Here!';

        console.log("Rendering - canRender: ", this.state.canRender)

        if (this.state.canRender) {
            console.log("Updating MSG");
            msg = this.state.deckId;
        }

        return (
            <div className="App">
                <p>Hello World</p>
                { msg }
            </div>
        );
    }
}

export default App;
