const URLBASE='https://deckofcardsapi.com/api/deck/'
const SHUFFLE='new/shuffle/?deck_count=1'

let deckId = undefined;

const docApiFetchJson = async (request) => 
{
	let response = await fetch(URLBASE + req);
	let json = await response.json();
	if (! json.success) throw "Fetch API Error - Bye!"

	console.log("Returning JSON:", json);
	return json;
}

export const newShuffledDeck = async () => {

	let json = await docApiFetchJson(SHUFFLE);
	deckId = json.deck_id;

	console.log("newShuffleDeck:", json, deckId);
	return json;
}
