import React from 'react';
import * as card from './cardapi';


class App extends React.Component {
    constructor() {
        super();
        this.state = { canRender: false, appJson: undefined };
    }

    async componentDidMount() {
        let json = await card.newShuffledDeck();
        this.setState({ canRender: true, appJson: json })
    }

    render() {
        let msg = 'Nothing here!';

        console.log("canRender: ", this.state.canRender)
        if (this.state.canRender) msg = this.state.appJson.deck_id;

        return (
            <div className="App">
                <p>Hello World</p>
                { msg }
            </div>
        );
    }
}

export default App;
