import React from 'react';
import * as card from './cardapi';


class App extends React.Component {
    canRender = false;
    appJson = "Nothing Here!";

    render() {
        console.log("RENDER APP!")

        if (! this.canRender) {

            console.log("canRender: ", this.canRender );

            ( card.newShuffledDeck() ).then( (j) => {


                    console.log("HI", j);

                    this.appJson = j;

                    this.canRender = true;

                }
            )
        }

        return (
            <div className="App">
                <p>Hello World</p>
                { this.appJson }
            </div>
        );
    }
}

export default App;
