import * as card from './cardapi';


class App extends React.Component {
    canRender = false;
    appJson = "Nothing Here!";

    render() {
        console.log("RENDER APP!")

        if (! canRender) {

            console.log("canRender: ", canRender );

            ( card.newShuffledDeck() ).then( (j) => {


                    console.log("HI", j);

                    appJson = j;

                    canRender = true;

                }
            )
        }

        return (
            <div className="App">
                <p>Hello World</p>
                { appJson }
            </div>
        );
    }
}

export default App;
