import React from 'react';

import Deck from './Deck';

import './App.css';

import CardView from './CardView';

class App extends React.Component {
    deck = undefined;

    constructor() {
        super();
        this.state = { canRender: false, deckId: undefined };
        this.deck = new Deck();
    }

    async componentDidMount() {
        let id = await this.deck.newDeck();

        this.setState({ canRender: true, deckId: id });
        console.log("DID MOUNT: ", this.state.deckId);
        await this.testDeck();
    }

    testDeck = async () => {
        const MAX=7;
        let card = undefined;

        console.log("testDeck - deckId: ", this.deck.id())

        for (let i=0; i<MAX; i++) {
            card = await this.deck.drawCard();
            console.log("CARD: ", i, card);
        }
    }

    render() {
        let msg = 'cannot render yet';

        console.log("Rendering - canRender: ", this.state.canRender)

        if (this.state.canRender) {
            msg = this.state.deckId;
        }

        return (
            <div className="hcenter">
            <div className="flexhline">
                <CardView imageName="https://deckofcardsapi.com/static/img/QS.png"/>
                <CardView imageName="https://deckofcardsapi.com/static/img/KS.png"/>
                <CardView imageName="https://deckofcardsapi.com/static/img/KS.png"/>
                <CardView imageName="https://deckofcardsapi.com/static/img/KS.png"/>
                <CardView imageName="https://deckofcardsapi.com/static/img/KS.png"/>
                <CardView imageName="https://deckofcardsapi.com/static/img/KS.png"/>
                <CardView imageName="https://deckofcardsapi.com/static/img/KS.png"/>
                <CardView imageName="https://deckofcardsapi.com/static/img/KS.png"/>

                <div className="fullh green">
                    <div className="red hcenter vcenter w50">
                        <p className="tac">Hello World {msg}</p>
                    </div>
                </div>
            </div>
            </div>
        );
    }
}

//                     
export default App;
